import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/AppointmentBooking.css';

const AppointmentBooking = () => {
    const { id } = useParams(); // URL'den doktorun ID'sini al
    const [doctorDetails, setDoctorDetails] = useState(null);
    const [errorMessage, setErrorMessage] = useState(''); // Hata mesajı yönetimi
    const navigate = useNavigate(); // Yönlendirme için kullanılır

    // Doktor detaylarını almak için useEffect
    useEffect(() => {
        const fetchDoctorDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/doctors/appointment/${id}`);
                if (!response.ok) {
                    if (response.status === 404) {
                        throw new Error('Doctor not found');
                    } else {
                        throw new Error('Failed to fetch doctor details');
                    }
                }
                const data = await response.json();
                setDoctorDetails(data);
            } catch (error) {
                console.error('Error fetching doctor details:', error);
                setErrorMessage(error.message); // Hata mesajını ayarla
            }
        };

        fetchDoctorDetails();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const appointmentData = {
            name: formData.get('name'),
            email: formData.get('email'),
            date: formData.get('date'),
            time: formData.get('time'),
            doctorId: id,
        };

        try {
            const response = await fetch('http://localhost:5001/api/appointments/book', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(appointmentData),
            });

            if (response.ok) {
                alert('Appointment booked successfully!');
                navigate('/'); // Ana sayfaya yönlendir
            } else {
                alert('Failed to book appointment. Please try again.');
            }
        } catch (error) {
            console.error('Error booking appointment:', error);
            alert('An error occurred. Please try again.');
        }
    };

    if (errorMessage) {
        return <p>{errorMessage}</p>; // Hata mesajını göster
    }

    if (!doctorDetails) {
        return null; // Doktor bilgileri yüklenirken hiçbir şey gösterme
    }

    return (
        <div className="appointment-booking-container">
            <div className="doctor-info">
                <h1 className="doctor-name">{doctorDetails.fullname}</h1>
                <p><strong>Specialization:</strong> {doctorDetails.area_of_interest}</p>
                <p><strong>Address:</strong> {doctorDetails.address}</p>
                <p><strong>Available Days:</strong> {doctorDetails.available_days?.join(', ')}</p>
                <p><strong>Available Hours:</strong> {doctorDetails.start_time} - {doctorDetails.end_time}</p>
                <p><strong>City:</strong> {doctorDetails.city}</p>
                <p><strong>Country:</strong> {doctorDetails.country}</p>
            </div>

            <div className="map-and-booking">
                <div className="google-map">
                    <iframe
                        title="Doctor Location"
                        width="100%"
                        height="400"
                        frameBorder="0"
                        style={{ border: 0 }}
                        src={`https://www.google.com/maps/embed/v1/place?key=AIzaSyDjSXEF1dPzfEcHvcTxKxYN14Easaxk9j0&q=${encodeURIComponent(
                            doctorDetails.address
                        )}`}
                        allowFullScreen
                    ></iframe>
                </div>

                <div className="booking-form">
                    <h2>Book an Appointment</h2>
                    <form onSubmit={handleSubmit}>
                        <label>
                            Your Name:
                            <input type="text" name="name" required />
                        </label>
                        <label>
                            Your Email:
                            <input type="email" name="email" required />
                        </label>
                        <label>
                            Date:
                            <input type="date" name="date" required />
                        </label>
                        <label>
                            Time:
                            <input type="time" name="time" required />
                        </label>
                        <button type="submit">Book Appointment</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default AppointmentBooking;
