import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminPanel = () => {
  const [doctors, setDoctors] = useState([
    { id: 1, name: 'Dr. John Doe', status: 'Pending' },
    { id: 2, name: 'Dr. Jane Smith', status: 'Approved' },
  ]);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('isAdmin'); // Admin oturum bilgisini temizle
    navigate('/'); // Ana sayfaya yönlendir
  };

  const approveDoctor = (id) => {
    setDoctors((prev) =>
      prev.map((doc) => (doc.id === id ? { ...doc, status: 'Approved' } : doc))
    );
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

      <h2 className="text-xl font-semibold mb-4">Pending Doctor Approvals</h2>
      <ul>
        {doctors
          .filter((doc) => doc.status === 'Pending')
          .map((doctor) => (
            <li key={doctor.id} className="mb-2 flex justify-between items-center">
              <span>{doctor.name}</span>
              <button
                className="bg-green-500 text-white px-4 py-1 rounded"
                onClick={() => approveDoctor(doctor.id)}
              >
                Approve
              </button>
            </li>
          ))}
      </ul>

      <h2 className="text-xl font-semibold mt-6 mb-4">Approved Doctors</h2>
      <ul>
        {doctors
          .filter((doc) => doc.status === 'Approved')
          .map((doctor) => (
            <li key={doctor.id}>{doctor.name}</li>
          ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
